import { useState, useEffect } from 'react';
import SplineObj from './SplineObj';
import NavBar from './NavBar';
import Home from './Home';

function LandingPage() {
  const [showNavbar, setShowNavbar] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const splineSection = document.getElementById('spline-section');
      if (splineSection) {
        const splineBottom = splineSection.offsetTop + splineSection.offsetHeight;
        setShowNavbar(window.scrollY >= splineBottom - 100);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="landing-page">
      {/* Landing Section with SplineObj */}
      <section id="spline-section" className="h-screen relative">
        <SplineObj />
      </section>

      {/* Sticky Navbar - only shows after scrolling past SplineObj */}
      <div className={`sticky top-0 z-50 transition-transform duration-300 ${
        showNavbar ? 'translate-y-0' : '-translate-y-full'
      }`}>
        <NavBar />
      </div>

      {/* Home Content */}
      <section className="min-h-screen">
        <Home />
      </section>
    </div>
  );
}

export default LandingPage;
