import { useEffect, useState } from 'react';
import axios from 'axios';

const API = 'http://localhost:5000/api/tasks';

function Main() {
  const [tasks, setTasks] = useState([]);
  const [form, setForm] = useState({ title: '', importance: 5, dueDate: '', completed: false, group: '' });
  const [search, setSearch] = useState('');
  const [sortBy, setSortBy] = useState('');
  const [hoverRating, setHoverRating] = useState(0);
  const [editingTask, setEditingTask] = useState(null);
  const [filter, setFilter] = useState('all');
  const [groups, setGroups] = useState(['Work', 'Home', 'School']);
  const [newGroup, setNewGroup] = useState('');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    const token = localStorage.getItem('authToken');
    if (!token) return alert('Please log in');
    try {
      const res = await axios.get(API, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(res.data);
    } catch (err) {
      console.error('Fetch tasks error:', err);
    }
  };

  const createTask = async () => {
    if (!form.title) return;
    const token = localStorage.getItem('authToken');
    await axios.post(API, form, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setForm({ title: '', importance: 5, dueDate: '', completed: false, group: '' });
    fetchTasks();
  };

  const updateTask = async (id, updatedTask) => {
    const token = localStorage.getItem('authToken');
    await axios.put(`${API}/${id}`, updatedTask, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setEditingTask(null);
    fetchTasks();
  };

  const deleteTask = async (id) => {
    const token = localStorage.getItem('authToken');
    await axios.delete(`${API}/${id}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    fetchTasks();
  };

  const handleEdit = (task) => {
    setEditingTask(task._id);
    setForm({
      title: task.title,
      importance: task.importance,
      dueDate: task.dueDate?.split('T')[0] || '',
      completed: task.completed,
      group: task.group || '',
    });
  };

  const handleSaveEdit = () => {
    if (editingTask) {
      updateTask(editingTask, form);
    }
  };

  const renderStars = () => {
    const stars = [];
    for (let i = 1; i <= 10; i++) {
      stars.push(
        <span
          key={i}
          className={`star ${i <= (hoverRating || form.importance) ? 'active' : ''}`}
          onMouseEnter={() => setHoverRating(i)}
          onMouseLeave={() => setHoverRating(0)}
          onClick={() => setForm({ ...form, importance: i })}
        >
          ‚òÖ
        </span>
      );
    }
    return stars;
  };

  const handleNewGroup = () => {
    if (newGroup.trim()) {
      setGroups([...groups, newGroup.trim()]);
      setNewGroup('');
    }
  };

  const filteredTasks = () => {
    const today = new Date().toISOString().split('T')[0];
    let list = tasks;

    if (filter === 'myday') {
      list = tasks.filter((t) => t.dueDate?.split('T')[0] === today);
    } else if (filter === 'important') {
      list = tasks.filter((t) => t.importance >= 7);
    } else if (filter === 'due') {
      list = [...tasks].sort((a, b) => new Date(a.dueDate) - new Date(b.dueDate));
    }

    return list.filter((t) => t.title.toLowerCase().includes(search.toLowerCase()));
  };

  return (
    <div className="flex min-h-screen">
      {/* Sidebar */}
      <aside className="w-64 p-6 bg-gray-100 border-r">
        <h2 className="text-xl font-bold mb-4">üìÇ Categories</h2>
        <ul className="space-y-2 mb-6">
          <li><button onClick={() => setFilter('myday')} className="hover:underline">üåû My Day</button></li>
          <li><button onClick={() => setFilter('important')} className="hover:underline">‚ùó Very Important</button></li>
          <li><button onClick={() => setFilter('due')} className="hover:underline">üìÖ Due Soon</button></li>
        </ul>
        <h3 className="font-semibold mb-2">Groups</h3>
        <ul className="space-y-1 mb-4">
          {groups.map((group) => (
            <li key={group} className="text-sm text-gray-700">{group}</li>
          ))}
        </ul>
        <input
          className="border p-1 rounded w-full mb-2 text-sm"
          placeholder="New group name"
          value={newGroup}
          onChange={(e) => setNewGroup(e.target.value)}
        />
        <button onClick={handleNewGroup} className="bg-blue-500 text-white text-sm px-2 py-1 rounded w-full">
          + Create Group
        </button>
      </aside>

      {/* Main content */}
      <main className="flex-1 p-6">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold">üìù Tasks</h1>
          <div>
            <button className="bg-gray-200 text-gray-700 px-3 py-1 rounded mr-2">Focus Mode</button>
            <button className="bg-black text-white px-3 py-1 rounded">AI Assist</button>
          </div>
        </div>

        {/* Task Form */}
        <div className="mb-6 flex flex-wrap gap-4">
          <input
            className="border p-2 rounded flex-1"
            placeholder="Task title"
            value={form.title}
            onChange={(e) => setForm({ ...form, title: e.target.value })}
          />
          <div className="flex items-center">
            <label className="mr-2">‚≠ê</label>
            {renderStars()}
          </div>
          <input
            className="border p-2 rounded"
            type="date"
            value={form.dueDate}
            onChange={(e) => setForm({ ...form, dueDate: e.target.value })}
          />
          <select
            className="border p-2 rounded"
            value={form.group}
            onChange={(e) => setForm({ ...form, group: e.target.value })}
          >
            <option value="">Select Group</option>
            {groups.map((group) => (
              <option key={group} value={group}>{group}</option>
            ))}
          </select>
          <button
            className="bg-purple-600 text-white px-4 py-2 rounded"
            onClick={editingTask ? handleSaveEdit : createTask}
          >
            {editingTask ? 'Save' : 'Finish'}
          </button>
        </div>

        {/* Search and Sort */}
        <div className="mb-4 flex flex-wrap gap-4">
          <input
            className="border p-2 rounded flex-1"
            placeholder="Search tasks..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <div className="flex gap-2">
            <button onClick={() => setSortBy('alpha')} className="bg-gray-200 px-3 py-2 rounded">A-Z</button>
            <button onClick={() => setSortBy('dueDate')} className="bg-gray-200 px-3 py-2 rounded">Due Date</button>
            <button onClick={() => setSortBy('importance')} className="bg-gray-200 px-3 py-2 rounded">Importance</button>
          </div>
        </div>

        {/* Task List */}
        <ul className="space-y-4">
          {filteredTasks().map((task) => (
            <li key={task._id} className="bg-gray-50 p-4 rounded-lg shadow flex justify-between items-center">
              <div>
                <h2 className={`font-semibold ${task.completed ? 'line-through text-gray-500' : ''}`}>
                  {task.title}
                </h2>
                <p className="text-sm text-gray-600">
                  Due: {task.dueDate ? new Date(task.dueDate).toLocaleDateString() : 'No date'} | 
                  ‚≠ê {task.importance} | 
                  Group: {task.group || 'None'}
                </p>
              </div>
              <div className="flex gap-2">
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => updateTask(task._id, { ...task, completed: !task.completed })}
                />
                <button className="bg-blue-500 text-white px-3 py-1 rounded" onClick={() => handleEdit(task)}>Edit</button>
                <button className="bg-red-500 text-white px-3 py-1 rounded" onClick={() => deleteTask(task._id)}>Delete</button>
              </div>
            </li>
          ))}
        </ul>
      </main>
    </div>
  );
}

export default Main;
