import './App.css';
import { useAuth } from './context/AuthContext';
import { useNavigate } from 'react-router-dom';

function NavBar() {
  const { isLoggedIn, user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <div className="nav-container">
        <div className="nav-brand" style={{ fontFamily: "'Balsamiq Sans', sans-serif" }}>FocusWin</div>
        <ul className="nav-menu">
          <li className="nav-item"><a href="/home" className="nav-link">Home</a></li>
          <li className="nav-item"><a href="#features" className="nav-link">Features</a></li>
          <li className="nav-item"><a href="#contact" className="nav-link">Contact</a></li>

          {!isLoggedIn ? (
            <>
              <li className="nav-item"><a href="/login" className="nav-link">Login</a></li>
            </>
          ) : (
            <>
              <li className="nav-item profile-dropdown">
                <div className="profile-circle">{user?.name?.charAt(0).toUpperCase()}</div>
                <div className="dropdown-content">
                  <button onClick={handleLogout} className="logout-btn">Logout</button>
                </div>
              </li>
            </>
          )}
        </ul>

        {!isLoggedIn && (
          <button className="get-started-btn" onClick={() => navigate('/signup')}>
            GET STARTED
            <svg className="arrow-icon" width="16" height="16" viewBox="0 0 16 16">
              <path d="M8 0l8 8-8 8-1.5-1.5L12 9H0V7h12L6.5 1.5z" fill="currentColor" />
            </svg>
          </button>
        )}
      </div>
    </nav>
  );
}

export default NavBar;
