import { useEffect, useState } from 'react';
import axios from 'axios';

const API = 'http://localhost:5000/api/tasks';

function Main() {
  const [tasks, setTasks] = useState([]);
  const [form, setForm] = useState({ title: '', importance: 5, dueDate: '', completed: false });
  const [search, setSearch] = useState('');
  const [sortBy, setSortBy] = useState('');
  const [hoverRating, setHoverRating] = useState(0);
  const [editingTask, setEditingTask] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      alert('Please log in to view tasks');
      return;
    }
    try {
      const res = await axios.get(API, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(res.data);
    } catch (err) {
      console.error('Fetch tasks error:', err);
    }
  };

  const createTask = async () => {
    if (!form.title) return;
    const token = localStorage.getItem('authToken');
    await axios.post(API, form, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setForm({ title: '', importance: 5, dueDate: '', completed: false });
    fetchTasks();
  };

  const deleteTask = async (id) => {
    const token = localStorage.getItem('authToken');
    await axios.delete(`${API}/${id}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    fetchTasks();
  };

  const updateTask = async (id, updatedTask) => {
    const token = localStorage.getItem('authToken');
    await axios.put(`${API}/${id}`, updatedTask, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setEditingTask(null);
    fetchTasks();
  };

  const sortedTasks = () => {
    let filtered = tasks.filter((t) =>
      t.title.toLowerCase().includes(search.toLowerCase())
    );
    if (sortBy === 'alpha') {
      filtered.sort((a, b) => a.title.localeCompare(b.title));
    } else if (sortBy === 'dueDate') {
      filtered.sort((a, b) => new Date(a.dueDate) - new Date(b.dueDate));
    } else if (sortBy === 'importance') {
      filtered.sort((a, b) => b.importance - a.importance);
    }
    return filtered;
  };

  const renderStars = () => {
    const stars = [];
    for (let i = 1; i <= 10; i++) {
      stars.push(
        <span
          key={i}
          className={`star ${i <= (hoverRating || form.importance) ? 'active' : ''}`}
          onMouseEnter={() => setHoverRating(i)}
          onMouseLeave={() => setHoverRating(0)}
          onClick={() => setForm({ ...form, importance: i })}
        >
          ‚òÖ
        </span>
      );
    }
    return stars;
  };

  const handleEdit = (task) => {
    setEditingTask(task._id);
    setForm({
      title: task.title,
      importance: task.importance,
      dueDate: task.dueDate ? task.dueDate.split('T')[0] : '',
      completed: task.completed,
    });
  };

  const handleSaveEdit = () => {
    if (editingTask) {
      updateTask(editingTask, form);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-lg">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">üìù Today Task</h1>
        <div>
          <button className="bg-gray-200 text-gray-700 px-3 py-1 rounded mr-2">Focus Mode</button>
          <button className="bg-black text-white px-3 py-1 rounded">AI Assist</button>
        </div>
      </div>

      <div className="mb-6 flex flex-col sm:flex-row gap-4">
        {editingTask ? (
          <>
            <input
              className="border p-3 rounded w-full sm:w-1/3"
              placeholder="Task title"
              value={form.title}
              onChange={(e) => setForm({ ...form, title: e.target.value })}
            />
            <div className="flex items-center gap-2 w-full sm:w-1/3">
              <label className="w-20">Importance:</label>
              <div className="flex">{renderStars()}</div>
            </div>
            <input
              className="border p-3 rounded w-full sm:w-1/3"
              type="date"
              value={form.dueDate}
              onChange={(e) => setForm({ ...form, dueDate: e.target.value })}
            />
            <button
              className="bg-purple-600 text-white px-6 py-2 rounded hover:bg-purple-700"
              onClick={handleSaveEdit}
            >
              Save
            </button>
            <button
              className="bg-gray-500 text-white px-6 py-2 rounded hover:bg-gray-600"
              onClick={() => setEditingTask(null)}
            >
              Cancel
            </button>
          </>
        ) : (
          <>
            <input
              className="border p-3 rounded w-full sm:w-1/3"
              placeholder="Task title"
              value={form.title}
              onChange={(e) => setForm({ ...form, title: e.target.value })}
            />
            <div className="flex items-center gap-2 w-full sm:w-1/3">
              <label className="w-20">Importance:</label>
              <div className="flex">{renderStars()}</div>
            </div>
            <input
              className="border p-3 rounded w-full sm:w-1/3"
              type="date"
              value={form.dueDate}
              onChange={(e) => setForm({ ...form, dueDate: e.target.value })}
            />
            <button
              className="bg-purple-600 text-white px-6 py-2 rounded hover:bg-purple-700 mb-6"
              onClick={createTask}
            >
              Finish
            </button>
          </>
        )}
      </div>

      <div className="mb-6 flex flex-col sm:flex-row gap-4">
        <input
          className="border p-3 rounded flex-1"
          placeholder="Search..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <div className="flex gap-2">
          <button onClick={() => setSortBy('alpha')} className="bg-gray-200 px-3 py-2 rounded">
            A-Z
          </button>
          <button onClick={() => setSortBy('dueDate')} className="bg-gray-200 px-3 py-2 rounded">
            Due Date
          </button>
          <button onClick={() => setSortBy('importance')} className="bg-gray-200 px-3 py-2 rounded">
            Importance
          </button>
        </div>
      </div>

      <ul className="space-y-4">
        {sortedTasks().map((task) => (
          <li key={task._id} className="bg-gray-50 p-4 rounded-lg shadow flex justify-between items-center">
            <div>
              <h2 className={`font-semibold ${task.completed ? 'line-through text-gray-500' : ''}`}>
                {task.title}
              </h2>
              <p className="text-sm text-gray-600">
                Due: {task.dueDate ? new Date(task.dueDate).toLocaleDateString() : 'No date'} | 
                Importance: ‚≠ê {task.importance} | 
                Completed At: {task.completedAt ? new Date(task.completedAt).toLocaleString() : 'N/A'}
              </p>
            </div>
            <div className="flex gap-2">
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => updateTask(task._id, { ...task, completed: !task.completed })}
              />
              <button
                className="bg-blue-500 text-white px-3 py-1 rounded"
                onClick={() => handleEdit(task)}
              >
                Edit
              </button>
              <button
                className="bg-red-500 text-white px-3 py-1 rounded"
                onClick={() => deleteTask(task._id)}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Main;
