import { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext';

const API_BASE = 'http://localhost:5000/api';

function Main() {
  const { username } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [tasks, setTasks] = useState([]);
  const [groups, setGroups] = useState([]); // Will now be an array of objects: [{_id, name}]
  const [form, setForm] = useState({
    title: '',
    importance: 5,
    dueDate: '',
    completed: false,
    group: '', // This will store the group NAME
  });
  const [search, setSearch] = useState('');
  const [editingTask, setEditingTask] = useState(null); // Stores the task object being edited
  const [hoverRating, setHoverRating] = useState(0);
  const [filter, setFilter] = useState('all'); // 'all' | 'myday' | 'important' | 'due' | group.name
  const [newGroup, setNewGroup] = useState('');

  useEffect(() => {
    // If the logged-in user does not match the URL, redirect to their own page
    if (user && user.name !== username) {
      navigate(`/main/${user.name}`, { replace: true });
    }
    // eslint-disable-next-line
  }, [user, username, navigate]);

  useEffect(() => {
    fetchAll();
  }, []);

  const fetchAll = async () => {
    try {
      const [taskRes, groupRes] = await Promise.all([
        axios.get(`${API_BASE}/tasks`, { withCredentials: true }),
        axios.get(`${API_BASE}/groups`, { withCredentials: true }),
      ]);
      setTasks(taskRes.data);
      setGroups(groupRes.data);
    } catch (err) {
      console.error('‚ùå Fetch error:', err);
      if (err.response?.status === 401) {
        alert('Session expired. Please log in again.');
        // Optional: redirect to login page
        // window.location.href = '/login';
      } else {
        alert(`Error fetching data: ${err.response?.data?.error || err.message}`);
      }
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    if (!form.title.trim()) return;

    if (editingTask) {
      await updateTask(editingTask._id, { ...editingTask, ...form });
    } else {
      await createTask();
    }
  };

  const createTask = async () => {
    await axios.post(`${API_BASE}/tasks`, form, { withCredentials: true });
    resetForm();
    await fetchAll();
  };

  const updateTask = async (id, data) => {
    await axios.put(`${API_BASE}/tasks/${id}`, data, { withCredentials: true });
    resetForm();
    await fetchAll();
  };

  const deleteTask = async (id) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      await axios.delete(`${API_BASE}/tasks/${id}`, { withCredentials: true });
      await fetchAll();
    }
  };
  
  const resetForm = () => {
    setEditingTask(null);
    setForm({ title: '', importance: 5, dueDate: '', completed: false, group: '' });
  };

  const handleEdit = (task) => {
    setEditingTask(task);
    setForm({
      title: task.title,
      importance: task.importance,
      dueDate: task.dueDate?.split('T')[0] || '',
      completed: task.completed,
      group: task.group || '',
    });
  };
  
  const handleNewGroup = async () => {
    if (!newGroup.trim()) return;
    try {
      await axios.post(`${API_BASE}/groups`, { name: newGroup.trim() }, { withCredentials: true });
      setNewGroup('');
      await fetchAll();
    } catch (error) {
      console.error('Group creation error:', error);
      const errorMessage = error.response?.data?.error || error.message || 'Failed to create group.';
      alert(errorMessage);
    }
  };

  const getFilterButtonClass = (filterName) => {
    return filter === filterName 
      ? "font-bold text-indigo-600 w-full text-left" 
      : "w-full text-left hover:text-indigo-600";
  };
  
  const renderStars = (isForm = true) => {
    const rating = isForm ? form.importance : 0;
    const setRating = isForm ? (i) => setForm({ ...form, importance: i }) : () => {};
    
    return Array.from({ length: 10 }, (_, i) => i + 1).map((i) => (
      <span
        key={i}
        className={`star cursor-pointer text-2xl ${i <= (hoverRating || rating) ? 'text-yellow-400' : 'text-gray-300'}`}
        onMouseEnter={() => setHoverRating(i)}
        onMouseLeave={() => setHoverRating(0)}
        onClick={() => setRating(i)}
      >
        ‚òÖ
      </span>
    ));
  };
  
  const today = new Date();
  today.setHours(0, 0, 0, 0);

  const filteredTasks = tasks
    .filter((task) => {
      switch (filter) {
        case 'myday':
          if (!task.dueDate) return false;
          const dueDate = new Date(task.dueDate);
          return dueDate.toDateString() === today.toDateString();
        case 'important':
          return task.importance >= 7;
        case 'all':
          return true;
        // This handles filtering by a group name
        default:
          return task.group === filter;
      }
    })
    .filter((task) => task.title.toLowerCase().includes(search.toLowerCase()))
    .sort((a, b) => {
        if (filter === 'due') {
            const dateA = a.dueDate ? new Date(a.dueDate) : new Date('9999-12-31');
            const dateB = b.dueDate ? new Date(b.dueDate) : new Date('9999-12-31');
            return dateA - dateB;
        }
        // Default sort by creation date (newest first)
        return new Date(b.createdAt) - new Date(a.createdAt);
    });

  return (
    <div className="flex h-screen bg-gray-50">
      {/* Sidebar */}
      <aside className="w-64 flex-shrink-0 p-6 bg-white border-r overflow-y-auto">
        <h2 className="text-xl font-bold mb-6 text-gray-800">Categories</h2>
        <ul className="space-y-3 mb-8">
          <li><button onClick={() => setFilter('all')} className={getFilterButtonClass('all')}>üìã All Tasks</button></li>
          <li><button onClick={() => setFilter('myday')} className={getFilterButtonClass('myday')}>üåû My Day</button></li>
          <li><button onClick={() => setFilter('important')} className={getFilterButtonClass('important')}>‚ùó Important</button></li>
          <li><button onClick={() => setFilter('due')} className={getFilterButtonClass('due')}>üìÖ By Due Date</button></li>
        </ul>

        <h3 className="font-semibold mb-3 text-gray-800">Groups</h3>
        <ul className="space-y-2 mb-4">
          {groups.map((group) => (
            <li key={group._id}>
              <button onClick={() => setFilter(group.name)} className={getFilterButtonClass(group.name)}>
                # {group.name}
              </button>
            </li>
          ))}
        </ul>
        <div className="flex items-center">
            <input
              className="border p-2 rounded-l-md w-full text-sm"
              placeholder="New group..."
              value={newGroup}
              onChange={(e) => setNewGroup(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleNewGroup()}
            />
            <button onClick={handleNewGroup} className="bg-indigo-600 text-white text-lg px-3 py-2 rounded-r-md hover:bg-indigo-700">
              +
            </button>
        </div>
      </aside>

      {/* Main content */}
      <main className="flex-1 p-8 overflow-y-auto">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">{filter === 'all' ? 'All Tasks' : filter}</h1>

        {/* Task Form */}
        <form onSubmit={handleFormSubmit} className="mb-8 p-4 bg-white rounded-lg shadow-sm">
            <input
                className="border-0 p-2 text-lg w-full focus:ring-0"
                placeholder="What's on your mind?"
                value={form.title}
                onChange={(e) => setForm({ ...form, title: e.target.value })}
            />
            <div className="mt-2 flex flex-wrap items-center gap-4 text-sm">
                <div className="flex items-center gap-1">{renderStars(true)}</div>
                <input
                    className="border p-2 rounded" type="date"
                    value={form.dueDate}
                    onChange={(e) => setForm({ ...form, dueDate: e.target.value })}
                />
                <select
                    className="border p-2 rounded" value={form.group}
                    onChange={(e) => setForm({ ...form, group: e.target.value })}
                >
                    <option value="">No Group</option>
                    {groups.map((g) => <option key={g._id} value={g.name}>{g.name}</option>)}
                </select>
                <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded-lg ml-auto hover:bg-green-600">
                    {editingTask ? 'Save Changes' : 'Add Task'}
                </button>
                {editingTask && <button type="button" onClick={resetForm} className="text-gray-500 hover:underline">Cancel</button>}
            </div>
        </form>

        {/* Search */}
        <div className="mb-4">
          <input
            className="border p-2 rounded-lg w-full"
            placeholder="Search tasks..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>

        {/* Task List */}
        <ul className="space-y-3">
          {filteredTasks.map((task) => (
            <li key={task._id} className="bg-white p-4 rounded-lg shadow-sm flex justify-between items-center transition-all hover:shadow-md">
              <div className="flex items-center gap-4">
                 <input
                  type="checkbox"
                  className="h-5 w-5 rounded text-indigo-600 focus:ring-indigo-500"
                  checked={task.completed}
                  onChange={() => updateTask(task._id, { completed: !task.completed })}
                />
                <div>
                  <h2 className={`font-semibold ${task.completed ? 'line-through text-gray-400' : 'text-gray-800'}`}>
                    {task.title}
                  </h2>
                  <p className="text-sm text-gray-500">
                    {task.dueDate ? `Due: ${new Date(task.dueDate).toLocaleDateString()}` : 'No due date'}
                    {task.group && ` ‚Ä¢ #${task.group}`}
                    {` ‚Ä¢ `} <span className="text-yellow-500">{'‚òÖ'.repeat(task.importance)}</span>
                  </p>
                </div>
              </div>
              <div className="flex gap-2">
                <button className="text-gray-500 hover:text-blue-600" onClick={() => handleEdit(task)}>Edit</button>
                <button className="text-gray-500 hover:text-red-600" onClick={() => deleteTask(task._id)}>Delete</button>
              </div>
            </li>
          ))}
        </ul>
        {filteredTasks.length === 0 && <p className="text-center text-gray-500 mt-8">No tasks here. Clear your mind!</p>}
      </main>
    </div>
  );
}

export default Main;
