import { createContext, useState, useContext, useEffect } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // Add a loading state

  useEffect(() => {
    // This function will run once when the component mounts
    const checkLoginStatus = async () => {
      try {
        // Ask the backend if we are logged in
        const response = await fetch('http://localhost:5000/api/auth/me', {
          credentials: 'include', // IMPORTANT: This sends the session cookie
        });

        if (response.ok) {
          const userData = await response.json();
          // If the server says we're logged in, update the state
          setIsLoggedIn(true);
          setUser(userData);
        } else {
          // If the server says we're not logged in, ensure state is cleared
          setIsLoggedIn(false);
          setUser(null);
        }
      } catch (error) {
        // If there's a network error, assume logged out
        console.error("Could not verify authentication", error);
        setIsLoggedIn(false);
        setUser(null);
      } finally {
        // We're done loading, whether we succeeded or failed
        setLoading(false);
      }
    };

    checkLoginStatus();
  }, []); // The empty array [] means this effect runs only once on mount

  const login = (userData) => {
    setIsLoggedIn(true);
    setUser(userData);
  };

  const logout = async () => {
    await fetch('http://localhost:5000/api/auth/logout', {
      method: 'POST',
      credentials: 'include'
    });
    setIsLoggedIn(false);
    setUser(null);
  };

  // While loading, you might want to show nothing or a spinner
  if (loading) {
    return <div>Loading...</div>; // Or a fancy spinner component
  }

  return (
    <AuthContext.Provider value={{ isLoggedIn, user, loading, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
