import { motion } from 'framer-motion';

export default function Home() {
  return (
    <div className="min-h-screen bg-[#0f0f0f] text-white flex flex-col md:flex-row items-center justify-between px-10 py-16 overflow-hidden">
      
      {/* LEFT: Text + CTA */}
      <motion.div
        className="md:w-1/2 w-full space-y-6 text-center md:text-left"
        initial={{ x: -100, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <h1 className="text-5xl md:text-6xl font-extrabold text-[#00ffa2]">
          FocusFirst
        </h1>
        <h2 className="text-2xl md:text-3xl font-semibold text-white">
          Turn every task into a win. <br /> Make every day your game.
        </h2>
        <p className="text-lg text-gray-300 max-w-lg mx-auto md:mx-0">
          You're not just checking boxes â€” you're leveling up.  
          <span className="text-[#00ffa2] font-medium"> You are the main character.</span>
        </p>
        <div className="pt-4">
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="bg-[#00ffa2] hover:bg-[#00dd88] text-black font-bold py-3 px-6 rounded-xl transition duration-300 shadow-md hover:shadow-[#00ffa2]/50"
          >
            Get Started
          </motion.button>
        </div>
      </motion.div>

      {/* RIGHT: Image */}
      <motion.div
        className="md:w-1/2 w-full flex items-center justify-center mt-10 md:mt-0"
        initial={{ x: 100, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <img
          src="https://cdn-dynmedia-1.microsoft.com/is/image/microsoftcorp/lqv7fip1-1-hero-photo-568x520?resMode=sharp2&op_usm=1.5,0.65,15,0&wid=1360&hei=765&qlt=100&fit=constrain"
          alt="Focus Productivity Visual"
          className="max-w-[90%] md:max-w-full rounded-xl shadow-2xl hover:scale-105 transition duration-500"
        />
      </motion.div>
    </div>
  );
}
