import { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext';

const API_BASE = 'http://localhost:5000/api';

function Main() {
  const { username } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [tasks, setTasks] = useState([]);
  const [groups, setGroups] = useState([]);
  const [form, setForm] = useState({
    title: '',
    importance: 5,
    dueDate: '',
    completed: false,
    group: '',
  });
  const [search, setSearch] = useState('');
  const [editingTask, setEditingTask] = useState(null);
  const [hoverRating, setHoverRating] = useState(0);
  const [filter, setFilter] = useState('all');
  const [newGroup, setNewGroup] = useState('');
  const [showNotifications, setShowNotifications] = useState(false);

  useEffect(() => {
    // If the logged-in user does not match the URL, redirect to their own page
    console.log('User object:', user);
    console.log('Username from URL:', username);
    if (user && user.name !== username) {
      console.log('Redirecting to:', `/main/${user.name}`);
      navigate(`/main/${user.name}`, { replace: true });
    }
    // eslint-disable-next-line
  }, [user, username, navigate]);

  useEffect(() => {
    fetchAll();
    // Check for due tasks every minute
    const interval = setInterval(checkDueTasks, 60000);
    return () => clearInterval(interval);
  }, []);

  const checkDueTasks = () => {
    const now = new Date();
    const sixHoursFromNow = new Date(now.getTime() + 6 * 60 * 60 * 1000);
    
    const dueSoonTasks = tasks.filter(task => {
      if (task.completed || !task.dueDate) return false;
      const dueDate = new Date(task.dueDate);
      return dueDate <= sixHoursFromNow && dueDate > now;
    });

    dueSoonTasks.forEach(task => {
      sendNotification(task);
    });
  };

  const sendNotification = async (task) => {
    // Web notification
    if ('Notification' in window && Notification.permission === 'granted') {
      new Notification('Task Due Soon!', {
        body: `${task.title} is due in less than 6 hours. Finish it fast!`,
        icon: '/favicon.ico',
        badge: '/favicon.ico'
      });
    }

    // Email notification (you'll need to implement this on backend)
    try {
      await axios.post(`${API_BASE}/notifications/email`, {
        taskId: task._id,
        taskTitle: task.title,
        dueDate: task.dueDate
      }, { withCredentials: true });
    } catch (error) {
      console.error('Failed to send email notification:', error);
    }
  };

  const requestNotificationPermission = () => {
    if ('Notification' in window) {
      Notification.requestPermission().then(permission => {
        if (permission === 'granted') {
          setShowNotifications(true);
        }
      });
    }
  };

  const fetchAll = async () => {
    try {
      const [taskRes, groupRes] = await Promise.all([
        axios.get(`${API_BASE}/tasks`, { withCredentials: true }),
        axios.get(`${API_BASE}/groups`, { withCredentials: true }),
      ]);
      setTasks(taskRes.data);
      setGroups(groupRes.data);
    } catch (err) {
      console.error('‚ùå Fetch error:', err);
      if (err.response?.status === 401) {
        alert('Session expired. Please log in again.');
      } else {
        alert(`Error fetching data: ${err.response?.data?.error || err.message}`);
      }
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    if (!form.title.trim()) return;

    if (editingTask) {
      await updateTask(editingTask._id, { ...editingTask, ...form });
    } else {
      await createTask();
    }
  };

  const createTask = async () => {
    await axios.post(`${API_BASE}/tasks`, form, { withCredentials: true });
    resetForm();
    await fetchAll();
  };

  const updateTask = async (id, data) => {
    await axios.put(`${API_BASE}/tasks/${id}`, data, { withCredentials: true });
    resetForm();
    await fetchAll();
  };

  const deleteTask = async (id) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      await axios.delete(`${API_BASE}/tasks/${id}`, { withCredentials: true });
      await fetchAll();
    }
  };
  
  const resetForm = () => {
    setEditingTask(null);
    setForm({ title: '', importance: 5, dueDate: '', completed: false, group: '' });
  };

  const handleEdit = (task) => {
    setEditingTask(task);
    setForm({
      title: task.title,
      importance: task.importance,
      dueDate: task.dueDate?.split('T')[0] || '',
      completed: task.completed,
      group: task.group || '',
    });
  };
  
  const handleNewGroup = async () => {
    if (!newGroup.trim()) return;
    try {
      await axios.post(`${API_BASE}/groups`, { name: newGroup.trim() }, { withCredentials: true });
      setNewGroup('');
      await fetchAll();
    } catch (error) {
      console.error('Group creation error:', error);
      const errorMessage = error.response?.data?.error || error.message || 'Failed to create group.';
      alert(errorMessage);
    }
  };

  const getFilterButtonClass = (filterName) => {
    return filter === filterName 
      ? "flex items-center gap-3 px-4 py-3 rounded-xl bg-gradient-to-r from-indigo-500 to-purple-600 text-white font-medium shadow-lg transition-all duration-200" 
      : "flex items-center gap-3 px-4 py-3 rounded-xl text-gray-600 hover:bg-gray-100 hover:text-indigo-600 font-medium transition-all duration-200";
  };
  
  const renderStars = (rating, isForm = false, onRatingChange = null) => {
    const currentRating = isForm ? rating : rating;
    
    return Array.from({ length: 10 }, (_, i) => i + 1).map((i) => (
      <span
        key={i}
        className={`star cursor-pointer text-lg transition-colors duration-200 ${
          i <= (hoverRating || currentRating) ? 'text-yellow-400' : 'text-gray-300'
        } hover:scale-110`}
        onMouseEnter={() => setHoverRating(i)}
        onMouseLeave={() => setHoverRating(0)}
        onClick={() => {
          if (onRatingChange) onRatingChange(i);
          if (isForm) setForm({ ...form, importance: i });
        }}
      >
        ‚òÖ
      </span>
    ));
  };
  
  const today = new Date();
  today.setHours(0, 0, 0, 0);

  const filteredTasks = tasks
    .filter((task) => {
      switch (filter) {
        case 'myday':
          if (!task.dueDate) return false;
          const dueDate = new Date(task.dueDate);
          return dueDate.toDateString() === today.toDateString();
        case 'important':
          return task.importance >= 7;
        case 'upcoming':
          if (task.completed || !task.dueDate) return false;
          const taskDueDate = new Date(task.dueDate);
          return taskDueDate > today;
        case 'all':
          return true;
        default:
          return task.group === filter;
      }
    })
    .filter((task) => task.title.toLowerCase().includes(search.toLowerCase()))
    .sort((a, b) => {
        if (filter === 'upcoming') {
            const dateA = a.dueDate ? new Date(a.dueDate) : new Date('9999-12-31');
            const dateB = b.dueDate ? new Date(b.dueDate) : new Date('9999-12-31');
            return dateA - dateB;
        }
        return new Date(b.createdAt) - new Date(a.createdAt);
    });

  const getTaskPriorityColor = (importance) => {
    if (importance >= 8) return 'border-l-4 border-red-500';
    if (importance >= 6) return 'border-l-4 border-orange-500';
    return 'border-l-4 border-green-500';
  };

  const getDueDateStatus = (dueDate) => {
    if (!dueDate) return { text: 'No due date', color: 'text-gray-400' };
    
    const due = new Date(dueDate);
    const now = new Date();
    const diffTime = due - now;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    if (diffDays < 0) return { text: 'Overdue', color: 'text-red-500' };
    if (diffDays === 0) return { text: 'Due today', color: 'text-orange-500' };
    if (diffDays === 1) return { text: 'Due tomorrow', color: 'text-yellow-500' };
    if (diffDays <= 3) return { text: `Due in ${diffDays} days`, color: 'text-blue-500' };
    return { text: `Due ${due.toLocaleDateString()}`, color: 'text-gray-500' };
  };

  return (
    <div className="flex h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      {/* Sidebar */}
      <aside className="w-80 flex-shrink-0 p-6 bg-white shadow-xl overflow-y-auto">
        <div className="mb-8">
          <h2 className="text-2xl font-bold mb-2 text-gray-800">Welcome back, {user?.name}!</h2>
          <p className="text-gray-600">Let's get things done today</p>
        </div>

        <div className="mb-8">
          <h3 className="text-lg font-semibold mb-4 text-gray-800">Quick Filters</h3>
          <div className="space-y-2">
            <button onClick={() => setFilter('all')} className={getFilterButtonClass('all')}>
              <span className="text-xl">üìã</span>
              <span>All Tasks</span>
            </button>
            <button onClick={() => setFilter('myday')} className={getFilterButtonClass('myday')}>
              <span className="text-xl">üåû</span>
              <span>My Day</span>
            </button>
            <button onClick={() => setFilter('important')} className={getFilterButtonClass('important')}>
              <span className="text-xl">‚ùó</span>
              <span>Important</span>
            </button>
            <button onClick={() => setFilter('upcoming')} className={getFilterButtonClass('upcoming')}>
              <span className="text-xl">üìÖ</span>
              <span>Upcoming</span>
            </button>
          </div>
        </div>

        <div className="mb-6">
          <h3 className="text-lg font-semibold mb-4 text-gray-800">Groups</h3>
          <div className="space-y-2 mb-4">
            {groups.map((group) => (
              <button 
                key={group._id} 
                onClick={() => setFilter(group.name)} 
                className={getFilterButtonClass(group.name)}
              >
                <span className="text-xl">#</span>
                <span>{group.name}</span>
              </button>
            ))}
          </div>
          
          <div className="flex items-center gap-2">
            <input
              className="flex-1 border border-gray-300 p-3 rounded-lg text-sm focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
              placeholder="New group..."
              value={newGroup}
              onChange={(e) => setNewGroup(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleNewGroup()}
            />
            <button 
              onClick={handleNewGroup} 
              className="bg-indigo-600 text-white p-3 rounded-lg hover:bg-indigo-700 transition-colors duration-200"
            >
              +
            </button>
          </div>
        </div>

        {/* Notification Settings */}
        <div className="border-t pt-6">
          <h3 className="text-lg font-semibold mb-4 text-gray-800">Notifications</h3>
          <button 
            onClick={requestNotificationPermission}
            className="w-full bg-green-500 text-white px-4 py-3 rounded-lg hover:bg-green-600 transition-colors duration-200"
          >
            Enable Notifications
          </button>
        </div>
      </aside>

      {/* Main content */}
      <main className="flex-1 p-8 overflow-y-auto">
        <div className="max-w-4xl mx-auto">
          <div className="mb-8">
            <h1 className="text-4xl font-bold text-gray-800 mb-2">
              {filter === 'all' ? 'All Tasks' : 
               filter === 'upcoming' ? 'Upcoming Tasks' : 
               filter === 'myday' ? 'My Day' : 
               filter === 'important' ? 'Important Tasks' : 
               `#${filter}`}
            </h1>
            <p className="text-gray-600">
              {filteredTasks.length} {filteredTasks.length === 1 ? 'task' : 'tasks'}
            </p>
          </div>

          {/* Task Form */}
          <div className="mb-8 bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
            <form onSubmit={handleFormSubmit}>
              <div className="flex items-center gap-4 mb-4">
                <input
                  className="flex-1 border-0 text-xl p-4 bg-gray-50 rounded-xl focus:ring-2 focus:ring-indigo-500 focus:bg-white transition-all duration-200"
                  placeholder="What's on your mind?"
                  value={form.title}
                  onChange={(e) => setForm({ ...form, title: e.target.value })}
                />
                <button 
                  type="submit" 
                  className="bg-gradient-to-r from-indigo-500 to-purple-600 text-white px-8 py-4 rounded-xl font-semibold hover:from-indigo-600 hover:to-purple-700 transition-all duration-200 shadow-lg"
                >
                  {editingTask ? 'Save Changes' : 'Add Task'}
                </button>
                {editingTask && (
                  <button 
                    type="button" 
                    onClick={resetForm} 
                    className="text-gray-500 hover:text-gray-700 px-4 py-4 rounded-xl hover:bg-gray-100 transition-all duration-200"
                  >
                    Cancel
                  </button>
                )}
              </div>
              
              <div className="flex flex-wrap items-center gap-6 text-sm">
                <div className="flex items-center gap-2">
                  <span className="text-gray-600 font-medium">Priority:</span>
                  <div className="flex gap-1">
                    {renderStars(form.importance, true)}
                  </div>
                </div>
                
                <div className="flex items-center gap-2">
                  <span className="text-gray-600 font-medium">Due Date:</span>
                  <input
                    className="border border-gray-300 p-2 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    type="date"
                    value={form.dueDate}
                    onChange={(e) => setForm({ ...form, dueDate: e.target.value })}
                  />
                </div>
                
                <div className="flex items-center gap-2">
                  <span className="text-gray-600 font-medium">Group:</span>
                  <select
                    className="border border-gray-300 p-2 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    value={form.group}
                    onChange={(e) => setForm({ ...form, group: e.target.value })}
                  >
                    <option value="">No Group</option>
                    {groups.map((g) => <option key={g._id} value={g.name}>{g.name}</option>)}
                  </select>
                </div>
              </div>
            </form>
          </div>

          {/* Search */}
          <div className="mb-6">
            <div className="relative">
              <input
                className="w-full border border-gray-300 p-4 pl-12 rounded-xl bg-white shadow-sm focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all duration-200"
                placeholder="Search tasks..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
              <span className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400">üîç</span>
            </div>
          </div>

          {/* Task List */}
          <div className="space-y-4">
            {filteredTasks.map((task) => {
              const dueStatus = getDueDateStatus(task.dueDate);
              return (
                <div 
                  key={task._id} 
                  className={`bg-white p-6 rounded-2xl shadow-sm border border-gray-100 hover:shadow-lg transition-all duration-200 ${getTaskPriorityColor(task.importance)}`}
                >
                  <div className="flex items-start justify-between">
                    <div className="flex items-start gap-4 flex-1">
                      <input
                        type="checkbox"
                        className="h-6 w-6 rounded-full text-indigo-600 focus:ring-indigo-500 mt-1"
                        checked={task.completed}
                        onChange={() => updateTask(task._id, { completed: !task.completed })}
                      />
                      
                      <div className="flex-1">
                        <h3 className={`text-lg font-semibold mb-2 ${
                          task.completed ? 'line-through text-gray-400' : 'text-gray-800'
                        }`}>
                          {task.title}
                        </h3>
                        
                        <div className="flex items-center gap-4 text-sm text-gray-600">
                          <span className={dueStatus.color}>{dueStatus.text}</span>
                          {task.group && (
                            <span className="bg-indigo-100 text-indigo-700 px-2 py-1 rounded-full text-xs font-medium">
                              #{task.group}
                            </span>
                          )}
                          <div className="flex items-center gap-1">
                            <span className="text-yellow-500">
                              {renderStars(task.importance)}
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>
                    
                    <div className="flex gap-2">
                      <button 
                        className="text-gray-400 hover:text-blue-600 p-2 rounded-lg hover:bg-blue-50 transition-all duration-200"
                        onClick={() => handleEdit(task)}
                      >
                        ‚úèÔ∏è
                      </button>
                      <button 
                        className="text-gray-400 hover:text-red-600 p-2 rounded-lg hover:bg-red-50 transition-all duration-200"
                        onClick={() => deleteTask(task._id)}
                      >
                        üóëÔ∏è
                      </button>
                    </div>
                  </div>
                </div>
              );
            })}
            
            {filteredTasks.length === 0 && (
              <div className="text-center py-12">
                <div className="text-6xl mb-4">üìù</div>
                <h3 className="text-xl font-semibold text-gray-600 mb-2">No tasks found</h3>
                <p className="text-gray-500">Create your first task to get started!</p>
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
