import { useState, useEffect } from 'react';
import SplineObj from './SplineObj';
import NavBar from './NavBar';
import Home from './Home';

function LandingPage() {
  const [showNavbar, setShowNavbar] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      // Show navbar when user scrolls down more than 70vh
      const scrollPosition = window.scrollY;
      const viewportHeight = window.innerHeight;
      const showThreshold = viewportHeight * 0.7;
      
      setShowNavbar(scrollPosition >= showThreshold);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="landing-page">
      {/* Landing Section with SplineObj */}
      <section id="spline-section" className="h-screen relative overflow-hidden">
        <SplineObj />
      </section>

      {/* Fixed Navbar - appears on scroll */}
      <nav className={`fixed top-0 left-0 right-0 z-50 transform transition-all duration-500 ease-in-out ${
        showNavbar 
          ? 'translate-y-0 opacity-100' 
          : '-translate-y-full opacity-0'
      }`}>
        <NavBar />
      </nav>

      {/* Home Content Section */}
      <section className="min-h-screen bg-white">
        {/* Add padding top when navbar is visible to prevent content overlap */}
        <div className={`transition-all duration-300 ${showNavbar ? 'pt-24' : 'pt-8'}`}>
          <Home />
        </div>
      </section>

      {/* Additional spacing to ensure smooth scrolling */}
      <div className="h-20"></div>
    </div>
  );
}

export default LandingPage;
