import { useEffect, useState } from 'react';
import axios from 'axios';

const API = 'http://localhost:5000/api/tasks';

function Main() {
  const [tasks, setTasks] = useState([]);
  const [form, setForm] = useState({ title: '', importance: 5, dueDate: '' });
  const [search, setSearch] = useState('');
  const [sortBy, setSortBy] = useState('');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    const res = await axios.get(API);
    setTasks(res.data);
  };

  const createTask = async () => {
    if (!form.title) return;
    await axios.post(API, form);
    setForm({ title: '', importance: 5, dueDate: '' });
    fetchTasks();
  };

  const deleteTask = async (id) => {
    await axios.delete(`${API}/${id}`);
    fetchTasks();
  };

  const updateTask = async (id, updatedTask) => {
    await axios.put(`${API}/${id}`, updatedTask);
    fetchTasks();
  };

  const sortedTasks = () => {
    let filtered = tasks.filter(t =>
      t.title.toLowerCase().includes(search.toLowerCase())
    );
    if (sortBy === 'alpha') {
      filtered.sort((a, b) => a.title.localeCompare(b.title));
    } else if (sortBy === 'dueDate') {
      filtered.sort((a, b) => new Date(a.dueDate) - new Date(b.dueDate));
    } else if (sortBy === 'importance') {
      filtered.sort((a, b) => b.importance - a.importance);
    }
    return filtered;
  };

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 10; i++) {
      stars.push(
        <span
          key={i}
          className={`cursor-pointer text-2xl ${i <= rating ? 'text-yellow-400' : 'text-gray-300'}`}
          onClick={() => setForm({ ...form, importance: i })}
        >
          ‚òÖ
        </span>
      );
    }
    return stars;
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-lg">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">üìù Today Task</h1>
        <div>
          <button className="bg-gray-200 text-gray-700 px-3 py-1 rounded mr-2">Focus Mode</button>
          <button className="bg-black text-white px-3 py-1 rounded">AI Assist</button>
        </div>
      </div>

      <div className="mb-6 flex flex-col sm:flex-row gap-4">
        <input
          className="border p-3 rounded w-full sm:w-1/3"
          placeholder="Task title"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
        />
        <div className="flex items-center gap-2 w-full sm:w-1/3">
          <label className="w-20">Importance:</label>
          <div className="flex">{renderStars(form.importance)}</div>
        </div>
        <input
          className="border p-3 rounded w-full sm:w-1/3"
          type="date"
          value={form.dueDate}
          onChange={(e) => setForm({ ...form, dueDate: e.target.value })}
        />
      </div>
      <button
        className="bg-purple-600 text-white px-6 py-2 rounded hover:bg-purple-700 mb-6"
        onClick={createTask}
      >
        Finish
      </button>

      <div className="mb-6 flex flex-col sm:flex-row gap-4">
        <input
          className="border p-3 rounded flex-1"
          placeholder="Search..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <div className="flex gap-2">
          <button onClick={() => setSortBy('alpha')} className="bg-gray-200 px-3 py-2 rounded">A-Z</button>
          <button onClick={() => setSortBy('dueDate')} className="bg-gray-200 px-3 py-2 rounded">Due Date</button>
          <button onClick={() => setSortBy('importance')} className="bg-gray-200 px-3 py-2 rounded">Importance</button>
        </div>
      </div>

      <ul className="space-y-4">
        {sortedTasks().map(task => (
          <li key={task._id} className="bg-gray-50 p-4 rounded-lg shadow flex justify-between items-center">
            <div>
              <h2 className={`font-semibold ${task.completed ? 'line-through text-gray-500' : ''}`}>
                {task.title}
              </h2>
              <p className="text-sm text-gray-600">
                Due: {task.dueDate ? new Date(task.dueDate).toLocaleDateString() : 'No date'} | ‚≠ê {task.importance}
              </p>
            </div>
            <div className="flex gap-2">
              <button
                className="bg-green-500 text-white px-3 py-1 rounded"
                onClick={() =>
                  updateTask(task._id, { ...task, completed: !task.completed })
                }
              >
                {task.completed ? 'Undo' : 'Done'}
              </button>
              <button
                className="bg-red-500 text-white px-3 py-1 rounded"
                onClick={() => deleteTask(task._id)}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Main;
